import v4 from 'uuid';
import { REMOVE_ALERT, SET_ALERT } from "../actions/types";

export const setAlert = (msg, alertType) => dispatch => {
    const id = v4;

    dispatch({
        type: SET_ALERT,
        payload: { msg, alertType, id }
    });
};
